mapper.select:
select *
from staff
where 1=1

    and sName like concat('%', #{ name }, '%')


    and sSalary <= #{maxSalary}


    and sSalary >= #{minSalary}


    and sDisabled = #{disabled}


    and sCompanyId = #{companyId}


    and sAddress like CONCAT('%', #{address}, '%')

mapper.update:
update staff
set sDisabled = "残疾"
where sID = #{id}
